import * as THREE from 'three';
import 'regenerator-runtime/runtime';
import GSAP from 'gsap';

import vertex from './shaders/vertex.glsl';
import fragment from './shaders/fragment.glsl';
import LoadImages from './LoadImages.js';

export default class Webgl {
    constructor() {
        this.sizes = {
            width: window.innerWidth,
            height: window.innerHeight
        }

        this.links = document.querySelectorAll('a');
        this.images = new LoadImages().images;

        this.camera = new THREE.PerspectiveCamera(78, window.innerWidth / window.innerHeight, 0.1, 1000);
        this.camera.position.z = 600;

        this.scene = new THREE.Scene();

        this.mesh = this.addImages();

        this.renderer = new THREE.WebGLRenderer({
            alpha: true
        });
        document.body.appendChild(this.renderer.domElement);
        this.renderer.setSize(this.sizes.width, this.sizes.height);
        this.canvas = this.renderer.domElement;

        this.resizeScene();
        this.viewSize = this.getViewSize();
        console.log(this.viewSize);

        this.animateObject();
    }

    resizeScene() {
        window.addEventListener('resize', () => {
            this.sizes.width = window.innerWidth;
            this.sizes.height = window.innerHeight;

            this.camera.aspect = this.sizes.width / this.sizes.height;
            this.camera.updateProjectionMatrix();

            this.rendererSize();
        });
    }

    getViewSize() {
        let distance = this.camera.position.z;
        let vFov = (this.camera.fov * Math.PI) / 180;
        let height = 2 * Math.tan(vFov / 2) * distance;
        let width = height * this.camera.aspect;
        return { width, height, vFov }
    }

    mousemove

    addImages() {
        this.images = this.images.map(img => {
            let pos = img.getBoundingClientRect();

            this.texture = new THREE.Texture(img);
            this.texture.needsUpdate = true;

            this.geometry = new THREE.PlaneGeometry(pos.width, pos.height, 1, 1);
            this.material = new THREE.RawShaderMaterial({
                vertexShader: vertex,
                fragmentShader: fragment,
                uniforms: {
                    uTexture: {
                        value: new THREE.TextureLoader().load(img.src)
                    },
                    uOffset: {
                        value: new THREE.Vector2(0.0, 0.0)
                    }
                }
            });

            this.mesh = new THREE.Mesh(this.geometry, this.material);

            this.scene.add(this.mesh);

            return {
                img: img,
                mesh: this.mesh,
                x: pos.top,
                y: pos.left,
                width: pos.width,
                height: pos.height
            }
        });
    }

    rendererSize() {
        this.renderer.setSize(this.sizes.width, this.sizes.height);
        this.renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    }

    animateObject() {
        this.renderer.render(this.scene, this.camera);

        window.requestAnimationFrame(() => {
            this.animateObject();
        });
    }
}
